mapper.update:
update member1 set password=#{password}, name=#{name}, phone=#{phone}, gender=#{gender}, birth=#{birth}, email=#{email}

	, profile=#{profile}

 where id=#{id}

mapper.select:
select * from (select a.*, rowNum rn from (select * from member1 


		${searchKey} like '%'||#{searchValue}||'%'


order by mno desc)a) where rn between #{startRow} and #{endRow}
